// Node 5: Format Sales - Split sales into individual records

const inputData = $input.all()[0].json;
const salesRecords = inputData.sales || [];

console.log('=== FORMATTING SALES ===');
console.log('Received sales count:', salesRecords.length);
console.log('Sales data:', JSON.stringify(salesRecords, null, 2));

// Return each sale as a separate item for Google Sheets to process
if (salesRecords.length > 0) {
  console.log('Splitting', salesRecords.length, 'sales into individual records');
  return salesRecords.map((sale, index) => {
    console.log(`Formatting sale ${index + 1}: ${sale.productName}`);
    return sale;
  });
} else {
  console.log('No sales to format');
  return [];
}

// Fixed Node 3: Handle Google Sheets Object Data Format

// Get the raw input data
const inputData = $input.all();
console.log('=== RAW INPUT DEBUG ===');
console.log('Input structure:', JSON.stringify(inputData, null, 2));

let salesRecords = [];
let actualData = null;

// Try multiple ways to access the Google Sheets data
if (inputData && inputData.length > 0) {
  const firstItem = inputData[0];
  console.log('First item keys:', Object.keys(firstItem));
  console.log('First item:', JSON.stringify(firstItem, null, 2));

  // Method 1: Check if data is in .json property as array
  if (firstItem.json && Array.isArray(firstItem.json)) {
    actualData = firstItem.json;
    console.log('âœ… Found data in .json as array');
  }
  // Method 2: Check if data is in .json property as object with values
  else if (firstItem.json && firstItem.json.values) {
    actualData = firstItem.json.values;
    console.log('âœ… Found data in .json.values');
  }
  // Method 3: Check if data is directly in the item
  else if (Array.isArray(firstItem)) {
    actualData = firstItem;
    console.log('âœ… Found data as direct array');
  }
  // Method 4: Check if multiple items represent rows
  else if (inputData.length > 1) {
    actualData = inputData;
    console.log('âœ… Using all input items as data rows');
  }
  // Method 5: Check various Google Sheets response formats
  else {
    console.log('ðŸ” Exploring all properties of first item:');
    for (const [key, value] of Object.entries(firstItem)) {
      console.log(`  ${key}:`, typeof value, Array.isArray(value) ? `(array length: ${value.length})` : '');
      if (key !== 'json' && Array.isArray(value)) {
        console.log(`    Sample from ${key}:`, JSON.stringify(value.slice(0, 2)));
      }
    }
  }
}

console.log('=== PROCESSING DATA ===');
console.log('Actual data:', JSON.stringify(actualData, null, 2));
console.log('Data type:', typeof actualData);
console.log('Is array:', Array.isArray(actualData));

// Process the data if we found it
if (Array.isArray(actualData) && actualData.length > 0) {
  console.log('Processing', actualData.length, 'rows');

  // Process each row
  for (let i = 0; i < actualData.length; i++) {
    const row = actualData[i];
    console.log(`Row ${i}:`, JSON.stringify(row, null, 2));

    let productId, productName, category, price, quantity;

    // Handle different row formats
    if (Array.isArray(row)) {
      // Standard array format
      productId = row[0];
      productName = row[1];
      category = row[2];
      price = parseFloat(row[3]) || 0;
      quantity = parseInt(row[4]) || 0;
    } else if (typeof row === 'object' && row !== null) {
      // Object format - try different property names
      productId = row['Product ID'] || row.productId || row['0'] || row.A;
      productName = row['Product Name'] || row.productName || row['1'] || row.B;
      category = row['Category'] || row.category || row['2'] || row.C;
      price = parseFloat(row['Price'] || row.price || row['3'] || row.D || 0);
      quantity = parseInt(row['Current Quantity'] || row.quantity || row['4'] || row.E || 0);

      // If still no data, try json property of the row
      if (!productId && row.json) {
        if (Array.isArray(row.json)) {
          productId = row.json[0];
          productName = row.json[1];
          category = row.json[2];
          price = parseFloat(row.json[3]) || 0;
          quantity = parseInt(row.json[4]) || 0;
        }
      }
    }

    console.log(`Extracted from row ${i}: ID="${productId}", Name="${productName}", Price=${price}, Qty=${quantity}`);

    // Create test sale if we have valid product data
    if (productId && productName && price > 0) {
      // Create a sale for ALL products (for testing)
      const orderNum = `ORD${Date.now().toString().slice(-6)}${i.toString().padStart(2, '0')}`;
      const soldQty = Math.floor(Math.random() * 3) + 1; // 1-3 items
      const totalAmount = price * soldQty;

      const saleRecord = {
        orderNo: orderNum,
        productId: productId,
        productName: productName,
        category: category || 'General',
        itemPrice: price,
        quantitySold: soldQty,
        totalAmount: parseFloat(totalAmount.toFixed(2)),
        saleDate: new Date().toISOString().split('T')[0],
        saleTime: new Date().toTimeString().split(' ')[0]
      };

      salesRecords.push(saleRecord);
      console.log(`ðŸŽ‰ SALE CREATED: ${productName} x${soldQty} = $${totalAmount.toFixed(2)}`);
    } else {
      console.log(`âŒ Skipping row ${i}: Missing required data (ID="${productId}", Name="${productName}", Price=${price})`);
    }
  }
} else {
  console.log('âŒ No valid data array found to process');
}

// Final result
const result = {
  sales: salesRecords,
  hasSales: salesRecords.length > 0,
  salesCount: salesRecords.length,
  message: salesRecords.length > 0 ? `Created ${salesRecords.length} test sales` : 'No sales created - check data format',
  rawInputDebug: {
    inputType: typeof inputData,
    inputLength: inputData ? inputData.length : 0,
    firstItemKeys: inputData && inputData.length > 0 ? Object.keys(inputData[0]) : [],
    actualDataType: typeof actualData,
    actualDataIsArray: Array.isArray(actualData),
    actualDataLength: Array.isArray(actualData) ? actualData.length : 0
  }
};

console.log('=== FINAL RESULT ===');
console.log('Sales created:', result.salesCount);
console.log('Has sales:', result.hasSales);
console.log('Raw input debug:', JSON.stringify(result.rawInputDebug, null, 2));

return [result];
